import React, { useState } from "react";
import { ButtonBar } from "@tea/component/buttonbar";

export default function ButtonBarExample() {
  const [value, setValue] = useState(1);

  return (
    <ButtonBar
      list={[
        { name: "1个月", value: 1 },
        { name: "2个月", value: 2 },
        { name: "3个月", value: 3 },
        { name: "半年", value: 6 },
        { name: "1年", value: 12 },
        { name: "2年", value: 24 },
        { name: "3年", value: 36, disabled: true },
      ]}
      selected={value}
      onSelect={({ value }) => setValue(+value)}
    />
  );
}
