import React from "react";
import { Icon } from "@tea/component/icon";
import { Bubble } from "@tea/component/bubble";

// 暂无 SVG：'records', 'sales', 'shutdown', 'running', 'deploy', 'links', 'client', 'alarm', 'try', 'new', 'favorable'

// prettier-ignore
const iconTypes = [
  'close', 'dismiss', 'delete', 'shopcart', 'more', 'folderclose', 'folderopen', 'notice', 'guide',
  'info', 'help', 'consult', 'realname', 'realnamev', 'show', 'hide', 'warning', 'error', 'pending',
  'infoblue', 'success', 'loading', 'sort', 'sortup', 'sortdown','btnback', 'plus', 'minus',
  'setting', 'pencil', 'copy', 'remind', 'phone', 'phonev', 'wechat', 'wechatv', 'drop',
  'email', 'emailv', 'qq', 'viewgrid', 'viewlist', 'monitor', 'download', 'filter',
  'convertip--blue', 'convertip', 'arrowup', 'arrowdown', 'arrowleft', 'arrowright', 'externallink',
  'refresh', 'fullscreen', 'fullscreenquit', 'trenddown', 'trendup',
];

export default function IconExample() {
  return iconTypes.map(type => (
    <Bubble key={type} content={`<Icon type="${type}" />`}>
      <Icon
        type={type}
        style={{ margin: "0 10px 10px 0", cursor: "pointer" }}
      />
    </Bubble>
  ));
}
