import React from "react";
import { mount } from "enzyme";
import { Popover } from "../Popover";

describe("Popover", () => {
  it("render correctly", () => {
    const wrapper = mount(
      <Popover
        placement="top-start"
        overlay={
          <div
            style={{
              display: "inline-block",
              background: "red",
              color: "yellow",
              padding: 2,
            }}
          >
            这是浮层内容
          </div>
        }
      >
        鼠标上来
      </Popover>
    );
    expect(wrapper.render()).toMatchSnapshot();
  });
});
