import React from "react";
import classNames from "classnames";

export interface RegionFlagProps {
  flag?: string;
}

export function RegionFlag({ flag = "Globe" }: RegionFlagProps) {
  const className = classNames({
    [`tea-flag`]: true,
    [`tea-flag-${flag}`]: true,
  });
  return <span className={className} />;
}
