import uniq from "../uniq";

test("uniq number or string", () => {
  expect(uniq([1, 2, 3, 4, 5])).toMatchSnapshot();
  expect(uniq([1, 2, 2, 3, 3, 3])).toMatchSnapshot();
  expect(uniq([1, 2, 1, 2, 1])).toMatchSnapshot();
  expect(uniq(["a", "b", "b", "c"])).toMatchSnapshot();
  expect(uniq(["1", 1, 2, "2", "2", 1])).toMatchSnapshot();
});
