import React, { Fragment } from "react";
import { BasicBar } from "@tea/component/basicbar";

export default function SimpleBasicBarExample() {
  const data = [
    { week: "周一", value: 10 },
    { week: "周二", value: 20 },
    { week: "周三", value: 50 },
    { week: "周四", value: 60 },
    { week: "周五", value: 20 },
    { week: "周六", value: 72 },
    { week: "周日", value: 98 },
  ];

  return (
    <Fragment>
      <BasicBar height={250} position="week*value" dataSource={data} />
    </Fragment>
  );
}
