import React, { useState, Fragment } from "react";
import { SearchBox } from "@tea/component/searchbox";
import { Switch } from "@tea/component/switch";

export default function SearchBoxExample() {
  const [multiline, setMultiline] = useState(false);
  return (
    <Fragment>
      <section>
        <SearchBox
          multiline={multiline}
          onSearch={console.log}
          onClear={() => console.log("clear")}
        />
      </section>
      <hr />
      <section>
        <Switch value={multiline} onChange={value => setMultiline(value)}>
          多行搜索
        </Switch>
      </section>
    </Fragment>
  );
}
