import React from "react";
export interface BeforeDestroyParams {
    /**
     * 导致模块要销毁的原因
     *
     * - `navigate` 表示用户要导航到其他路由下，所以销毁当前模块
     * - `unload` 表示浏览器选项卡被刷新/关闭，所以销毁当前模块
     */
    type: "navigate" | "unload";
    /**
     * 用户即将到达的页面路由，只有在 `type` 为 `navigate` 的时候会有这个参数
     */
    url?: string;
}
/**
 * 返回 false 将取消模块销毁
 */
export declare type BeforeDestroyListener = (params: BeforeDestroyParams) => string | false | void;
declare type BeforeDestroyContextProps = {
    setBeforeDestroyListener?: (listener: BeforeDestroyListener) => void;
};
export declare const BeforeDestroyContext: React.Context<BeforeDestroyContextProps>;
/**
 * @param listener 页面即将销毁或者跳转时调用
 * @param deps
 *
 * @example
 * ```js
function Component() {
  useBeforeDestroy(({ type, url }) => {
    return "确认离开当前页面？";
    // return 字符串时将弹出确认框：是（跳转）/否（阻止跳转）
    // return false 时将阻止跳转逻辑
  }, []);
  return <div>Component</div>
}
```
 */
export declare const useBeforeDestroy: (listener: BeforeDestroyListener, deps?: readonly any[]) => void;
export {};
