import React from "react";
import { TableAddon } from "../TableProps";
/**
 * `removeable` 插件用于支持表格可删除行的样式及操作。
 */
export interface RemoveableOptions<Record = any> {
    /**
     * 删除操作回调
     */
    onRemove?: (rowKey: string, context: {
        event: React.SyntheticEvent;
        record: Record;
    }) => void;
    /**
     * 列宽度，可以指定 CSS 属性或数字 (单位：px)
     * @default 46
     */
    width?: string | number;
    /**
     * 参考 Icon 组件[图标类型](/component/icon)
     *
     * @default "dismiss"
     */
    icon?: string;
}
export declare function removeable({ onRemove, width, icon, }: RemoveableOptions): TableAddon;
