import { hashObject } from "../hash-object";

describe("hashObject", () => {
  it("works correctly", () => {
    expect(hashObject({ a: "b" })).toMatchSnapshot();
  });

  it("should throw if trying to hash non plain object", () => {
    class Foo {}
    expect(() => hashObject(Foo)).toThrow(RangeError);
  });
});
