import React from "react";
import { CascaderSingle } from "./CascaderSingle";
import { CascaderMultiple } from "./CascaderMultiple";
import { CascaderProps, CascaderMultipleProps } from "./CascaderProps";

function isMultiple(props: any): props is CascaderMultipleProps {
  return !!props.multiple;
}

export function Cascader(props: CascaderProps) {
  if (isMultiple(props)) {
    return <CascaderMultiple {...props} />;
  }
  return <CascaderSingle {...props} />;
}

Cascader.defaultLabelAlign = "middle";
