import React from "react";
import { Icon } from "../icon";
import { useConfig } from "../_util/config-context";

export interface ModalMessageProps {
  /**
   * 要提示的消息
   */
  message?: React.ReactNode;

  /**
   * 可选的详细解释
   */
  description?: React.ReactNode;

  /**
   * 提示图标
   */
  icon?: "error" | "warning" | "pending" | "infoblue" | "success";
}

export function ModalMessage({
  icon,
  message,
  description,
}: ModalMessageProps) {
  const { classPrefix } = useConfig();
  return (
    <div className={`${classPrefix}-media`}>
      {icon && (
        <div className={`${classPrefix}-media__left`}>
          <Icon type={icon} size="l" />
        </div>
      )}
      <div className={`${classPrefix}-media__body`}>
        {message && (
          <h3 className={`${classPrefix}-dialog__messagetitle`}>{message}</h3>
        )}
        {description && (
          <div className={`${classPrefix}-dialog__messagetext`}>
            {description}
          </div>
        )}
      </div>
    </div>
  );
}
