import React, { useState } from "react";
import { PopConfirm } from "@tencent/tea-component/lib/popconfirm";
import { Button } from "@tencent/tea-component/lib/button";

export default function PopConfirmExample() {
  const [confirmVisible, setConfirmVisible] = useState(false);

  return (
    <PopConfirm
      title="确定要解除关联？"
      message="解除后，配置将不再生效，也不做关联。"
      visible={confirmVisible}
      onVisibleChange={setConfirmVisible}
      footer={
        <>
          <Button
            type="link"
            onClick={() => {
              setConfirmVisible(false);
              console.log("已解除");
            }}
          >
            解除
          </Button>
          <Button
            type="text"
            onClick={() => {
              setConfirmVisible(false);
              console.log("已取消");
            }}
          >
            取消
          </Button>
        </>
      }
      placement="top-start"
    >
      <Button type="text">解除关联</Button>
    </PopConfirm>
  );
}
