import React from "react";
import { SelectMultiple } from "@tencent/tea-component/lib/select";
import { Form } from "@tencent/tea-component/lib/form";
import { Tag } from "@tencent/tea-component/lib/tag";
import { Tooltip } from "@tencent/tea-component/lib/tooltip";

export default function MultiSelectExample() {
  return (
    <Form>
      <Form.Item label="看电影吃什么">
        <SelectMultiple
          options={[
            { value: "gz", text: "瓜子", tooltip: "百煮入味香" },
            {
              value: "hs",
              text: "花生",
              tooltip: "花生已售罄",
            },
            { value: "bmf", text: "爆米花", tooltip: "焦糖味" },
            { value: "kl", text: "可乐", tooltip: "肥宅快乐水" },
            { value: "pj", text: "啤酒" },
            { value: "kqs", text: "矿泉水" },
            { value: "bbz", text: "八宝粥" },
          ]}
          allOption={{
            value: "all",
            text: "我都要",
            tooltip: "胖子是没有前途的",
          }}
        />
      </Form.Item>
      <Form.Item label="看完电影吃什么">
        <SelectMultiple
          staging={false}
          appearance="button"
          defaultValue={["gz"]}
          options={[
            { value: "gz", text: "瓜子", tooltip: "百煮入味香" },
            {
              value: "hs",
              text: "花生",
              disabled: true,
              tooltip: "花生已售罄",
            },
            { value: "bmf", text: "爆米花", tooltip: "焦糖味" },
            { value: "kl", text: "可乐", tooltip: "肥宅快乐水" },
            { value: "pj", text: "啤酒" },
            { value: "kqs", text: "矿泉水" },
            { value: "bbz", text: "八宝粥" },
          ]}
          button={options =>
            options.length ? (
              <Tooltip title={options.map(o => o.text).join(", ")}>
                <div>
                  <Tag.Group>
                    {options.map(o => (
                      <Tag key={o.value}>{o.text}</Tag>
                    ))}
                  </Tag.Group>
                </div>
              </Tooltip>
            ) : (
              "请选择"
            )
          }
        />
      </Form.Item>
    </Form>
  );
}
