import React from "react";
import { Select } from "@tencent/tea-component/lib/select";

const options = Array.from({ length: 5000 }).map((_, i) => ({
  value: `${i}`,
  text: `Option ${i}`,
}));

export default function SelectExample() {
  return (
    <Select
      size="m"
      type="simulate"
      boxSizeSync
      appearance="button"
      options={options}
      listHeight={600}
    />
  );
}
